function kibiBytesToSize(bytes) {
if (bytes == 0) return '0 B';
var kibi = 1024, // or 1000
sizes = ['KiB', 'MiB', 'GiB', 'TiB', 'PiB', 'EiB', 'ZiB', 'YiB'],
i = Math.floor(Math.log(bytes) / Math.log(kibi));
sizes[-1] = 'B';
return (bytes / Math.pow(kibi, i)).toFixed(2) + ' ' + sizes[i];
}

